import {
  createAction,
  configureStore,
  createReducer,
  getDefaultMiddleware,
} from "@reduxjs/toolkit";
import { PostType, Posts } from "../../lib/types";

export const getAllPosts = createAction<Posts>("getPosts");
const middleware = [...getDefaultMiddleware()];
export type State = {
  posts: Posts;
  isFetching: boolean;
  error: boolean;
};

const defaulState: State = {
  posts: [],
  isFetching: false,
  error: false,
};

const rootReducer = createReducer(defaulState, (builder) => {
  builder.addCase(getAllPosts, (state, action) => {
    state.posts.push(...action.payload);
  });
});

export const store = configureStore({reducer:rootReducer, middleware})
