import { useState,useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { Container } from "../components/Container";
import { getAllPosts, State } from "../redux/store/store";
import Link from "next/link";
import {getPosts} from '../lib/api'

function HomePage({ data }) {
  const dispatch = useDispatch();
  const [kek, setKek] = useState<any>(null)
  useEffect(() => {
    const handlePopulate = () => {
      dispatch(getAllPosts(data));
    };
    handlePopulate();
  }, []);
  const handleAddDataFancy = () => {
    getPosts().then(data => setKek(data))
  };
  return (
    <div>
      {kek !== null && <pre>{JSON.stringify(kek)}</pre>}
      <button onClick={handleAddDataFancy}>Fetch fancy</button>
      <Link href="/posts/new">
        <a>Create new post</a>
      </Link>
      <Container posts={data} />
    </div>
  );
}

HomePage.getInitialProps = async ({ ctx }) => {
  const res = await axios.get(`https://simple-blog-api.crew.red/posts`);
  const data = res.data;
  return { data };
};

export default HomePage;
