import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { Container } from "../components/Container";
import { getAllPosts, State } from "../redux/store/store";
import Link from "next/link";

function HomePage({ data }) {
  const dispatch = useDispatch();

  useEffect(() => {
    const handlePopulate = () => {
      dispatch(getAllPosts(data));
    };
    handlePopulate();
  }, []);
  const handleAddDataFancy = async () => {
    try {
      const res = await axios.get(`https://simple-blog-api.crew.red/posts`);
      const ids = res.data.map((post) => post.id);
      await Promise.all(
        ids.map(async (elem) => {
          const content = await axios({
            method: "GET",
            url: `https://simple-blog-api.crew.red/posts/${elem}`,
            params: {
              _embed: "comments",
            },
          });
        })
      );
      console.log(ids);
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div>
      <button onClick={handleAddDataFancy}>Fetch fancy</button>
      <Link href="/posts/new">
        <a>Create new post</a>
      </Link>
      <Container posts={data} />
    </div>
  );
}

HomePage.getInitialProps = async ({ ctx }) => {
  const res = await axios.get(`https://simple-blog-api.crew.red/posts`);
  const data = res.data;
  return { data };
};

export default HomePage;
