  const handlePopulate = async () => {
    try {
      const res = await axios({
        method: "GET",
        url: "https://simple-blog-api.crew.red/posts",
      });
      dispatch(getAllPosts(res.data));
    } catch (e) {
      console.log(e);
    }
  };
  return (
    <div>
      <p>whatever</p>
      <pre>{JSON.stringify(posts)}</pre>
    </div>
  );
}

export default HomePage;
